import React, { useState, useEffect } from 'react';
import PaginationComponent from '../pagination/pagination';



import './styles.css';

require('es6-promise').polyfill();
require('isomorphic-fetch');

export default function App() {
  const [data, setData] = useState([]);
  const [q, setQ] = useState('');
  const [searchColumns, setSearchColumns] = useState([
    "bank_name",
  ]);

  const [selectedRow, setSelectedRow] = useState(-1);
    const handleRow = (event) => {
      setSelectedRow(event);
    };
  const [currentPage, setcurrentPage] = useState(1);
  const [maxPageNumberLimit, setmaxPageNumberLimit] = useState(15);
  useEffect(() => {
    fetch('https://vast-shore-74260.herokuapp.com/banks?city=MUMBAI')
      .then((response) => response.json())
      .then((json) => setData(json));
  }, []);

  function search(rows) {
 
    return rows.filter((row) =>
      searchColumns.some(
        (column) =>
          row[column]
            .toString()
            .toLowerCase()
            .indexOf(q.toLowerCase()) > -1,
      ),
    );
  }

  const columns = data[0] && Object.keys(data[0]);
  return (
    <div>
      <div>
        <input
          type='text'
          value={q}
          onChange={(e) => {setQ(e.target.value);
          setcurrentPage(1);
          }
          }
        />
        
        {columns &&
          columns.map((column) => (
            <label>
              <input
                type='checkbox'
                checked={searchColumns.includes(column)}
                onChange={(e) => {
                  const checked = searchColumns.includes(column);
                  setSearchColumns((prev) =>
                    checked
                      ? prev.filter((sc) => sc !== column)
                      : [...prev, column],
                  );
                }}
              />
              {column}
            </label>
          ))}
      </div>
      <div>
        <PaginationComponent handleRow={handleRow} setSelectedRow={setSelectedRow} selectedRow={selectedRow} currentPage={currentPage} setcurrentPage={setcurrentPage} data={data} search={search}/>
      </div>
    </div>
  );
}
