import React, { useEffect, useState } from "react";
import Datatable from "../datatable";
import Favourites from "../Favourites/favourites";
import "./styles.css";

function PaginationComponent({
  isLoading,
  data,
  search,
  currentPage,
  setcurrentPage,
  showFavourites,
}) {
  const [itemsPerPage, setitemsPerPage] = useState(10);

  const [pageNumberLimit, setpageNumberLimit] = useState(10);
  const [maxPageNumberLimit, setmaxPageNumberLimit] = useState(20);
  const [minPageNumberLimit, setminPageNumberLimit] = useState(0);
  const favourites = [];
  const handleClick = (event) => {
    setcurrentPage(Number(event.target.id));
  };

  const pages = [];
  for (let i = 1; i <= Math.ceil(search(data).length / itemsPerPage); i++) {
    pages.push(i);
  }

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = search(data).slice(indexOfFirstItem, indexOfLastItem);
  const currentItems1 = search(favourites).slice(
    indexOfFirstItem,
    indexOfLastItem
  );

  const renderPageNumbers = pages.map((number) => {
    if (number < maxPageNumberLimit + 1 && number > minPageNumberLimit) {
      return (
        <li
          key={number}
          id={number}
          onClick={handleClick}
          className={currentPage == number ? "active" : null}
        >
          {number}
        </li>
      );
    } else {
      return null;
    }
  });

  const handleNextbtn = () => {
    setcurrentPage(currentPage + 1);

    if (currentPage + 1 > maxPageNumberLimit) {
      setmaxPageNumberLimit(maxPageNumberLimit + pageNumberLimit);
      setminPageNumberLimit(minPageNumberLimit + pageNumberLimit);
    }
  };

  const handlePrevbtn = () => {
    setcurrentPage(currentPage - 1);

    if ((currentPage - 1) % pageNumberLimit == 0) {
      setmaxPageNumberLimit(maxPageNumberLimit - pageNumberLimit);
      setminPageNumberLimit(minPageNumberLimit - pageNumberLimit);
    }
  };

  let pageIncrementBtn = null;
  if (pages.length > maxPageNumberLimit) {
    pageIncrementBtn = <li onClick={handleNextbtn}> &hellip; </li>;
  }

  let pageDecrementBtn = null;
  if (minPageNumberLimit >= 1) {
    pageDecrementBtn = <li onClick={handlePrevbtn}> &hellip; </li>;
  }

  const handleLoadMore = () => {
    setitemsPerPage(itemsPerPage + 5);
  };

  const handleLoadLess = () => {
    if (itemsPerPage > 10) setitemsPerPage(itemsPerPage - 5);
  };

  return (
    <>
      {!showFavourites ? (
        <Datatable
          currentPage={currentPage}
          isLoading={isLoading}
          data={search(currentItems)}
          favourites={favourites}
        />
      ) : (
        <Favourites
          currentPage={currentPage}
          isLoading={isLoading}
          data={search(currentItems1)}
        />
      )}
      <ul className="pageNumbers">
        <li>
          <button
            className="loadmore"
            onClick={handlePrevbtn}
            disabled={currentPage == pages[0] ? true : false}
          >
            Prev
          </button>
        </li>
        {pageDecrementBtn}
        {renderPageNumbers}
        {pageIncrementBtn}

        <li>
          <button
            onClick={handleNextbtn}
            disabled={currentPage == pages[pages.length - 1] ? true : false}
            className="loadmore"
          >
            Next
          </button>
        </li>
      </ul>
      <button onClick={handleLoadMore} className="loadmore">
        Load More
      </button>
      &nbsp; &nbsp;
      <button onClick={handleLoadLess} className="loadmore">
        show less
      </button>
    </>
  );
}

export default PaginationComponent;
