import { Button, Icon } from "@material-ui/core";
import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useHistory } from "react-router-dom";
import useData from "../hooks/useData";

export default function Datatable({
  isLoading,
  data,
  currentPage,
  favourites,
  setFavourites,
}) {
  const history = useHistory();
  const columns = data[0] && Object.keys(data[0]);
  const [selectedRow, setSelectedRow] = useState(-1);
  const [selectedRowPage, setSelectedRowPage] = useState(-1);
  const handleClick = (item) => history.push(`/bank-details/${item}`);
  const setRemove = (item) =>
    favourites.indexOf(favourites.filter((data) => data.ifsc == item).ifsc);
  const { setIfsc } = useData();

  return (
    <table className="styled-table">
      <thead>
        <tr>
          <th>Favourites</th>
          {data[0] && columns.map((heading) => <th>{heading}</th>)}
        </tr>
      </thead>
      <tbody>
        {data.map((row, index) => (
          <tr
            key={index}
            onClick={(e) => {
              setSelectedRow(index);
              setSelectedRowPage(currentPage);
              handleClick(row.ifsc);
              setIfsc(row.ifsc);
            }}
            className={
              selectedRow == index && selectedRowPage === currentPage
                ? "active-row"
                : null
            }
          >
            <td>
              {!favourites.filter((item) => item.ifsc == row.ifsc).length >
              0 ? (
                <Button
                  style={{ background: "#00adff" }}
                  onClick={(e) => {
                    if (!e) var e = window.event;
                    e.cancelBubble = true;
                    if (e.stopPropagation) e.stopPropagation();
                    setFavourites([...favourites, row]);
                  }}
                >
                  Mark
                </Button>
              ) : (
                <Button
                  style={{ background: "orange" }}
                  onClick={(e) => {
                    if (!e) var e = window.event;
                    e.cancelBubble = true;
                    if (e.stopPropagation) e.stopPropagation();

                    favourites.splice(favourites.indexOf(row.ifsc), 1);
                  }}
                >
                  unmark
                </Button>
              )}
            </td>
            {columns.map((column) => (
              <td>{row[column]}</td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
}
