import React, { useState } from 'react';

export default function Datatable({ isLoading,data ,currentPage}) {
  const columns = data[0] && Object.keys(data[0]);
  const [selectedRow, setSelectedRow] = useState(-1);
  const [selectedRowPage, setSelectedRowPage] = useState(-1);
  return (
    <table className="styled-table">
      <thead>
        <tr>
          {data[0] && columns.map((heading) => <th>{heading}</th>)}
        </tr>
      </thead>
      <tbody>
        {data.map((row,index) => (
          <tr key={index}   onClick={() => {setSelectedRow(index);
          setSelectedRowPage(currentPage);
          }
          } className={selectedRow ==index && selectedRowPage=== currentPage ? "active-row" : null} >
            {columns.map((column) => (
              <td>{row[column]} {index}</td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
}
