import React from "react";
import { useEffect, useState } from "react";

const useData = (props) => {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useData(false);
  const [formData, setFormData] = useState({ category: "", city: "MUMBAI" });
  useEffect(() => {
    setIsLoading(true);
    fetch(`https://vast-shore-74260.herokuapp.com/banks?city=${formData.city}`)
      .then((response) => response.json())
      .then((json) => {
        setData(json);
        setIsLoading(false);
      });
  }, [formData.city]);

  return {
    data,
    setData,
    isLoading,
    setIsLoading,
    formData,
    setFormData,
  };
};

export default useData;
