import { Button, Icon } from "@material-ui/core";
import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useHistory } from "react-router-dom";
import useData from "../hooks/useData";

export default function Datatable({ isLoading, data, currentPage }) {
  const history = useHistory();
  const columns = data[0] && Object.keys(data[0]);
  const [selectedRow, setSelectedRow] = useState(-1);
  const [selectedRowPage, setSelectedRowPage] = useState(-1);
  const handleClick = (item) => history.push(`/bank-details/${item}`);
  const { setIfsc, favourites, setFavourites } = useData();

  return (
    <table className="styled-table">
      <thead>
        <tr>
          <th>Favourites</th>
          {data[0] && columns.map((heading) => <th>{heading}</th>)}
        </tr>
      </thead>
      <tbody>
        {data.map((row, index) => (
          <tr
            key={index}
            onClick={(e) => {
              setSelectedRow(index);
              setSelectedRowPage(currentPage);
              handleClick(row.ifsc);
              setIfsc(row.ifsc);
            }}
            className={
              selectedRow == index && selectedRowPage === currentPage
                ? "active-row"
                : null
            }
          >
            <td>
              <Button
                style={{ background: "#00adff" }}
                onClick={(e) => {
                  if (!e) var e = window.event;
                  e.cancelBubble = true;
                  if (e.stopPropagation) e.stopPropagation();
                  setFavourites({ ...favourites, ...row });
                  alert(row.ifsc);
                  console.log(favourites);
                }}
              >
                {" "}
                select
              </Button>
            </td>
            {columns.map((column) => (
              <td>{row[column]}</td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
}
