import React, { useState, useEffect } from 'react';
import PaginationComponent from '../pagination/pagination';



import './styles.css';

require('es6-promise').polyfill();
require('isomorphic-fetch');

export default function App() {
  const [data, setData] = useState([]);
  const [q, setQ] = useState('');
  const [searchColumns, setSearchColumns] = useState([
    "bank_name",
  ]);
  const [isLoading, setIsLoading] = useState(false);
 

  const handleSelectChange = (object, action) => {
    let name = action.name;
    let value = object.value;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };
  const List = [
    { value: "bank_name", label: "Bank Name" },
    { value: "ifsc", label: "IFSC Code" },
    { value: "bank_id", label: "Bank ID" },
  ];

  const [formData, setFormData] = useState({category:""});
  const [currentPage, setcurrentPage] = useState(1);
  const [maxPageNumberLimit, setmaxPageNumberLimit] = useState(15);
  useEffect(() => {
    setIsLoading(true);
    fetch('https://vast-shore-74260.herokuapp.com/banks?city=MUMBAI')
      .then((response) => response.json())
      .then((json) => 
     { 
      setData(json);
      setIsLoading(false);
     }
      );
  }, []);

  function search(rows) {
 
    return rows.filter((row) =>
      searchColumns.some(
        (column) =>
          row[column]
            .toString()
            .toLowerCase()
            .indexOf(q.toLowerCase()) > -1,
      ),
    );
  }

  const columns = data[0] && Object.keys(data[0]);
  return (
    <div>
      <div>
        <input
          type='text'
          value={q}
          onChange={(e) => {setQ(e.target.value);
          setcurrentPage(1);
          }
          }
        />
        
        <Select
                          styles={styles}
                          name="category"
                          onChange={handleSelectChange}
                          placeholder="Select"
                          options={List}
                          theme={(theme) => ({
                            ...theme,
                            colors: {
                              ...theme.colors,
                              text: "black",
                              primary25: "white",
                              primary: "#24cebd",
                              primary50: "#f5fcfb",
                            },
                          })}
                        ></Select>



        {columns &&
          columns.map((column) => (
            <label>
              <input
                type='checkbox'
                checked={searchColumns.includes(column)}
                onChange={(e) => {
                  const checked = searchColumns.includes(column);
                  setSearchColumns((prev) =>
                    checked
                      ? prev.filter((sc) => sc !== column)
                      : [...prev, column],
                  );
                }}
              />
              {column}
            </label>
          ))}
      </div>
      <div>
        <PaginationComponent isLoading={isLoading} currentPage={currentPage} setcurrentPage={setcurrentPage} data={data} search={search}/>
      </div>
    </div>
  );
}
